import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Post } from './model/model';
import { PostService } from './service/post.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'day39-demo';

  postForm!: FormGroup

  post!: Post 
  posts: Post[] = []
  imageData = ""
  blob!: Blob


  constructor(private router: Router, private fb: FormBuilder, private postSvc: PostService){}

  ngOnInit(): void {
    if(!this.postSvc.imageData){
      this.router.navigate(['/'])
      return
    }
    this.imageData = this.cameraSvc.imageData
    this.postForm = this.fb.group({
      title: this.fb.control<string>(''), 
      complain: this.fb.control<string>('')
    })
    this.blob = this.dataURItoBlob(this.imageData)
    console.log(">>> blob:", this.blob)
 }



}
